import React, { useState } from "react";
import Card from "../UI/Card";
import Button from "../UI/Button";
import classes from "./AddUser.module.css";

const AddUser = (props) => {
  // States
  const [enterName, setAddName] = useState("");
  const [enterAge, setAddAge] = useState("");

  // handlers
  const addUserHandler = (event) => {
    event.preventDefault()
    console.log (addAge, addName)
  };
  const nameHandler = (event) => {
    setAddName(event.target.value);
  };
  const ageHandler = (event) => {
    setAddAge(event.target.value);
  };

  return (
    <Card className={classes.input}>
      <form onSubmit = {addUserHandler}>
        <label htmlFor="username"> Username</label>
        <input id="username" type="text" value={addName} onChange={nameHandler} />
        <label htmlFor="age"> Age(Years)</label>
        <input id="age" type="number" value={addAge} onChange={ageHandler} />
        <Button type="submit" >
          Add user
        </Button>
      </form>
    </Card>
  );
};

export default AddUser;
